package string

fun findAnagrams(s: String, p: String): List<Int> {
    if (s.length < p.length) return emptyList()

    val targetHash = p.sumBy { it.toInt() }

    val window = p.length
    val result = mutableListOf<Int>()
    var prev = s.substring(0, window - 1).sumBy { it.toInt() }

    var i = window - 1
    while (i < s.length) {

        val hash = prev + s[i].toInt()

        if (hash == targetHash) {
            // check actual letters
            val startIndex = (i - window + 1)

            if (isAnagram(s.substring(startIndex, startIndex + window), p)) {
                // add index to result
                result.add(startIndex)
            }
        }
        // save prev value
        prev = hash - s[i - window + 1].toInt()
        i++
    }

    return result
}

private fun isAnagram(s: String, p: String): Boolean {
    val temp = Array(26) { 0 }

    s.forEach {
        temp[it-'a'] +=1
    }

    p.forEach {
        temp[it-'a'] -=1
    }

    return temp.none { it > 0 }
}

